import React,{useState} from 'react'

const AdminStoreForm = () => {
    
    const [input, setInput] = useState({
        StoreName:"",
        SerialNumber:"",
        MaterialNumber:"",
        MaterialDesc:"",
        OpeningBalance:"",
        RecievedQuantity:"",
        IssueQuantity:"",
        BalanceQuantity:"",
        Date:"",
    });
    const handleInputForm = (e) => {
        console.log(e);
        let { name, value } = e.target;
    
        setInput({ ...input, [name]: value });
    };
    const PostInput = async (e) => {
        e.preventDefault();
    
        const {StoreName,SerialNumber,MaterialNumber,MaterialDesc,OpeningBalance,RecievedQuantity,IssueQuantity,BalanceQuantity,Date } = input;
    
        const res = await fetch("/userinput", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            StoreName,
            SerialNumber,
            MaterialNumber,
            MaterialDesc,
            OpeningBalance,
            RecievedQuantity,
            IssueQuantity,
            BalanceQuantity,
            Day_and_Date,
          }),
        });
    
        const data = await res.json();
    
        if (res.status === 422 || !data) {
          //window.alert("Invalid Registration");
          console.log("Invalid Input");
        } else {
          window.alert("Input Successful");
          console.log("Input Successful");
        }
      };
    return (
        <div className="inputForm">
          <div className="form">
<h2 id="title">Admin Store</h2>
<div className="container">
  <form method="post">
    <div className="row">
      <div className="col-25">
        <label htmlFor="snum">SerialNumber</label>
      </div>
      <div className="col-75">
        <input type="text" id="snum" name="SerialNumber" placeholder="Your serial number" autoComplete="off"
        value={input.SerialNumber}
        onChange={handleInputForm} />
      </div>
    </div>
    <div className="row">
      <div className="col-25">
        <label htmlFor="mnum">MaterialNumber</label>
      </div>
      <div className="col-75">
        <input type="text" id="mnum" name="MaterialNumber" placeholder="Your material number" autoComplete="off"
        value={input.MaterialNumber}
        onChange={handleInputForm} />
      </div>
    </div>
    <div className="row">
      <div className="col-25">
        <label htmlFor="mtd">MaterialDescription</label>
      </div>
      <div className="col-75">
        <input type="text" id="mtd" name="meterialdesc" placeholder="MaterialDescription" autoComplete="off"
        value={input.MaterialDesc}
        onChange={handleInputForm} />
      </div>
    </div>
    <div className="row">
      <div className="col-25">
        <label htmlFor="ob">OpeningBalance</label>
      </div>
      <div className="col-75">
        <input type="text" id="ob" name="OpeningBalance" placeholder="OpeningBalance" autoComplete="off"
        value={input.OpeningBalance}
        onChange={handleInputForm} />
      </div>
    </div>
    <div className="row">
      <div className="col-25">
        <label htmlFor="rq">RecievedQuantity</label>
      </div>
      <div className="col-75">
        <input type="text" id="rq" name="RecievedQuantity" placeholder="RecievedQuantity" autoComplete="off"
        value={input.RecievedQuantity}
        onChange={handleInputForm} />
      </div>
    </div>
    <div className="row">
      <div className="col-25">
        <label htmlFor="iq">IssueQuantity</label>
      </div>
      <div className="col-75">
        <input type="text" id="iq" name="IssueQuantity" placeholder="IssueQuantity" autoComplete="off"
        value={input.IssueQuantity}
        onChange={handleInputForm} />
      </div>
    </div>
    <div className="row">
      <div className="col-25">
        <label htmlFor="bq">BalanceQuantity</label>
      </div>
      <div className="col-75">
        <input type="text" id="bq" name="BalanceQuantity" placeholder="BalanceQuantity" autoComplete="off"
        value={input.BalanceQuantity}
        onChange={handleInputForm} />
      </div>
    </div>
    <div className="row">
      <div className="col-25">
        <label htmlFor="date">Date</label>
      </div>
      <div className="col-75">
        <input type="text" id="date" name="Date" placeholder="Date" autoComplete="off"
        value={input.Day_and_Date}
        onChange={handleInputForm} />
      </div>
    </div>

  </form>
</div>
</div>
            
        </div>
    )
}

export default AdminStoreForm
