import React,{useState} from 'react'

const AdminStoreForm = () => {
    
    const [input, setInput] = useState({
        StoreName:"",
        SerialNumber:"",
        MaterialNumber:"",
        MaterialDesc:"",
        OpeningBalance:"",
        RecievedQuantity:"",
        IssueQuantity:"",
        BalanceQuantity:"",
        Date:"",
    });
    const handleInputForm = (e) => {
        console.log(e);
        let { name, value } = e.target;
    
        setInput({ ...input, [name]: value });
    };
    const PostInput = async (e) => {
        e.preventDefault();
    
        const {StoreName,SerialNumber,MaterialNumber,MaterialDesc,OpeningBalance,RecievedQuantity,IssueQuantity,BalanceQuantity,Date } = input;
    
        const res = await fetch("/userinput", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            StoreName,
            SerialNumber,
            MaterialNumber,
            MaterialDesc,
            OpeningBalance,
            RecievedQuantity,
            IssueQuantity,
            BalanceQuantity,
            Date,
          }),
        });
    
        const data = await res.json();
    
        if (res.status === 422 || !data) {
          //window.alert("Invalid Registration");
          console.log("Invalid Input");
        } else {
          window.alert("Input Successful");
          console.log("Input Successful");
        }
      };
    return (
        <div className="inputForm">
            
        </div>
    )
}

export default AdminStoreForm
