import React,{useState} from 'react'
import { Grid,Paper, Avatar, TextField, Button, Typography,Link } from '@material-ui/core'
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import {useHistory} from 'react-router-dom'
import axios from 'axios';



const Login=()=>{

    const history = useHistory();
    const [user,setUser]=useState({
        username:"",
        password:""
    })
    let name,value;
    const handleInputs=(e)=>{
        name=e.target.name;
        value=e.target.value;

        setUser({...user,[name]:value})
    }

    const PostData = async (e)=>{
        e.preventDefault();
        const {username,password} = user;
        const url="http://localhost:3001/login"
        const res =Axios.post(url,{
            method:"POST",
            headers:{
                "Context-Type":"application/json"
            },
            body:JSON.stringify({
                username,password
            }).then(res=>{res.json()})
            .then(history.push("/headquater"))

        })

        //const data =await res.json();
        //if(data.status===422 || !data){
        //    console.log("invalid registration")
        //}
        //else if(data.status===201){
          //  console.log("registration successfull")
            //history.push("/headquater") 
        //}
    }


    const paperStyle={padding :20,height:'70vh',width:280, margin:"80px auto"}
    const avatarStyle={backgroundColor:'#1bbd7e'}
    const btnstyle={margin:'8px 0'}
    return(
        <Grid>
            <Paper elevation={10} style={paperStyle}>
                <Grid align='center'>
                     <Avatar style={avatarStyle}><LockOutlinedIcon/></Avatar>
                    <h2>Sign In</h2>
                </Grid>
                <TextField label='Username' placeholder='Enter username' fullWidth required name="username" value={user.username} onChange={handleInputs}/>
                <TextField label='Password' placeholder='Enter password' fullWidth required name="password" value={user.password} onChange={handleInputs}/>
                <FormControlLabel
                    control={
                    <Checkbox
                        name="checkedB"
                        color="primary"
                    />
                    }
                    label="Remember me"
                 />
                <Button type='submit' color='primary' variant="contained" style={btnstyle,{outline:'none'}} fullWidth value="login" onClick={PostData} >Login</Button>
                <Typography >
                     <Link href="#" >
                        Forgot password ?
                </Link>
                </Typography>
                <Typography > Do you have an account ?
                     <Link href="/signup" >
                        Sign Up 
                </Link>
                </Typography>
            </Paper>
        </Grid>
    )
}

export default Login
