import React, { useState } from "react";
import { NavLink, useHistory } from "react-router-dom";
import './Signup.css'
const Signup = () => {
  const history = useHistory();
  const [user, setUser] = useState({
    name: "",
    email: "",
    phone: "",
    work: "",
    password: "",
    cpassword: "",
  });

  const handleInputs = (e) => {
    console.log(e);
    // name = e.target.name;
    // value = e.target.value;
    let { name, value } = e.target;

    setUser({ ...user, [name]: value });
  };

  const PostData = async (e) => {
    e.preventDefault();

    const { name, email, phone, work, password, cpassword } = user;

    const res = await fetch("/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name,
        email,
        phone,
        work,
        password,
        cpassword,
      }),
    });

    const data = await res.json();

    if (res.status === 422 || !data) {
      window.alert("Invalid Registration");
      console.log("Invalid Registration");
    } else {
      window.alert("Registration Successful");
      console.log("valid Registration");
      history.push("/login");
    }
  };

  return (
    <>
      <div class="wrapper">
    <div class="title">
Login Form</div>
<form method="post">
      <div class="field">
      <input
                  type="text"
                  name="name"
                  id="name"
                  autoComplete="off"
                  value={user.name}
                  onChange={handleInputs}
                  placeholder="Enter Your Name"
                />
        <label>Name</label>
      </div>
      <div class="field">
      <input
                  type="email"
                  name="email"
                  id="email"
                  autoComplete="off"
                  value={user.email}
                  onChange={handleInputs}
                  placeholder="Enter Your Email"
                />
        <label>Email Address</label>
      </div><div class="field">
      <input
                  type="phone"
                  name="phone"
                  id="phone"
                  autoComplete="off"
                  value={user.phone}
                  onChange={handleInputs}
                  placeholder="Enter Your Phone Number"
                />
        <label>Phone</label>
      </div><div class="field">
      <input
                  type="text"
                  name="work"
                  id="work"
                  autoComplete="off"
                  value={user.work}
                  onChange={handleInputs}
                  placeholder="Enter Your Profession"
                />
        <label>Work</label>
      </div>
      <div class="field">
      <input
                  type="password"
                  name="password"
                  id="password"
                  autoComplete="off"
                  value={user.password}
                  onChange={handleInputs}
                  placeholder="Enter Your Password"
                />
        <label>Password</label>
      </div>
      <div class="field">
      <input
                  type="password"
                  name="cpassword"
                  id="cpassword"
                  autoComplete="off"
                  value={user.cpassword}
                  onChange={handleInputs}
                  placeholder="Confirm Your Password"
                />
        <label>CPassword</label>
      </div>
      <div class="field">
      <input
                  type="submit"
                  name="signup"
                  id="signup"
                  className="form-submit"
                  value="Register"
                  onClick={PostData}
                />
      </div>
<div class="signup-link">
  <a href="/login">Register</a></div>
</form>
</div>
    </>
  );
};

export default Signup;
