import React,{useState} from 'react'
import './AdminStoreForm.css'

const AdminStoreForm = () => {
    
    const [input, setInput] = useState({
        StoreName:"",
        SerialNumber:"",
        MaterialNumber:"",
        MaterialDesc:"",
        OpeningBalance:"",
        RecievedQuantity:"",
        IssueQuantity:"",
        BalanceQuantity:"",
        Date:"",
    });
    const handleInputForm = (e) => {
        console.log(e);
        let { name, value } = e.target;
    
        setInput({ ...input, [name]: value });
    };
    const PostInput = async (e) => {
        e.preventDefault();
    
        const {StoreName,SerialNumber,MaterialNumber,MaterialDesc,OpeningBalance,RecievedQuantity,IssueQuantity,BalanceQuantity,Day_and_Date } = input;
    
        const res = await fetch("/userinput", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            StoreName,
            SerialNumber,
            MaterialNumber,
            MaterialDesc,
            OpeningBalance,
            RecievedQuantity,
            IssueQuantity,
            BalanceQuantity,
            Day_and_Date,
          }),
        });
    
        const data = await res.json();
    
        if (res.status === 422 || !data) {
          //window.alert("Invalid Registration");
          console.log("Invalid Input");
        } else {
          window.alert("Input Successful");
          console.log("Input Successful");
        }
      };
    return (
      <div className="wrapper">
      <div className="title">
  Login Form</div>
  <form method="post">
        <div className="field">
        <input
                    type="email"
                    name="email"
                    id="email"
                    autoComplete="off"
                    value={email}
                    onChange={(e)=>setEmail(e.target.value)}
                    placeholder="Enter Your Email"
                  />
          <label>Email Address</label>
        </div>
  <div className="field">
  <input
                    type="password"
                    name="password"
                    id="password"
                    value={password}
                    onChange={(e)=>setPassword(e.target.value)}
                    autoComplete="off"
                    placeholder="Enter Your Password"
                  />
          <label>Password</label>
        </div>
  <div className="content">
          <div className="checkbox">
            <input type="checkbox" id="remember-me" />
            <label htmlFor="remember-me">Remember me</label>
          </div>
  <div className="pass-link">
  </div>
  </div>
  <div className="field">
  <input
                    type="submit"
                    name="signin"
                    id="signin"
                    className="form-submit"
                    onClick={loginUser}
                    value="Log In"
                  />
        </div>
  <div className="signup-link">
  Not a member? <a href="/signup">Signup now</a></div>
  </form>
  </div>
    )
}

export default AdminStoreForm
