import React,{useState} from 'react'
import './AdminStoreForm.css'

const AdminStoreForm = () => {
    
    const [input, setInput] = useState({
        StoreName:"",
        SerialNumber:"",
        MaterialNumber:"",
        MaterialDesc:"",
        OpeningBalance:"",
        RecievedQuantity:"",
        IssueQuantity:"",
        BalanceQuantity:"",
        Date:"",
    });
    const handleInputForm = (e) => {
        console.log(e);
        let { name, value } = e.target;
    
        setInput({ ...input, [name]: value });
    };
    const PostInput = async (e) => {
        e.preventDefault();
    
        const {StoreName,SerialNumber,MaterialNumber,MaterialDesc,OpeningBalance,RecievedQuantity,IssueQuantity,BalanceQuantity,Day_and_Date } = input;
    
        const res = await fetch("/userinput", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            StoreName,
            SerialNumber,
            MaterialNumber,
            MaterialDesc,
            OpeningBalance,
            RecievedQuantity,
            IssueQuantity,
            BalanceQuantity,
            Day_and_Date,
          }),
        });
    
        const data = await res.json();
    
        if (res.status === 422 || !data) {
          //window.alert("Invalid Registration");
          console.log("Invalid Input");
        } else {
          window.alert("Input Successful");
          console.log("Input Successful");
        }
      };
    return (
      <html className="html">
        <body className="body">
        <div className="wrapper">
      <div className="title">
  Input Details</div>
  <form method="post">
  <div className="field">
        <input
                    type="text/number"
                    name="StoreName"
                    id="StoreName"
                    autoComplete="off"
                    value={input.StoreName}
                    onChange={handleInputForm}
                    placeholder="Enter Store"
                  />
          <label>Store Name</label>
        </div>
        <div className="field">
        <input
                    type="text"
                    name="SerialNumber"
                    id="SerialNumber"
                    autoComplete="off"
                    value={input.SerialNumber}
                    onChange={handleInputForm}
                    placeholder="Enter SerialNumber"
                  />
          <label>Serial Number</label>
        </div>
  <div className="field">
  <input
                    type="text"
                    name="MaterialNumber"
                    id="MaterialNumber"
                    value={input.MaterialNumber}
                    onChange={handleInputForm}
                    autoComplete="off"
                    placeholder="Enter MaterialNumber"
                  />
          <label>MaterialNumber</label>
        </div>
        <div className="field">
  <textarea
                    type="text"                
                    name="MaterialDesc"
                    id="MaterialDesc"
                    value={input.MaterialDesc}
                    onChange={handleInputForm}
                    autoComplete="off"
                    placeholder="Enter MaterialDesc"
                  />
          <label>MaterialDesc</label>
        </div><div className="field">
  <input
                    type="text"
                    name="OpeningBalance"
                    id="OpeningBalance"
                    value={input.OpeningBalance}
                    onChange={handleInputForm}
                    autoComplete="off"
                    placeholder="Enter OpeningBalance"
                  />
          <label>OpeningBalance</label>
        </div><div className="field">
  <input
                    type="text"
                    name="RecievedQuantity"
                    id="RecievedQuantity"
                    value={input.RecievedQuantity}
                    onChange={handleInputForm}
                    autoComplete="off"
                    placeholder="Enter RecievedQuantity"
                  />
          <label>RecievedQuantity</label>
        </div><div className="field">
  <input
                    type="text"
                    name="IssueQuantity"
                    id="IssueQuantity"
                    value={input.IssueQuantity}
                    onChange={handleInputForm}
                    autoComplete="off"
                    placeholder="Enter IssueQuantity"
                  />
          <label>IssueQuantity</label>
        </div>
        <div className="field">
  <input
                    type="text"
                    name="BalanceQuantity"
                    id="BalanceQuantity"
                    value={input.BalanceQuantity}
                    onChange={handleInputForm}
                    autoComplete="off"
                    placeholder="Enter BalanceQuantity"
                  />
          <label>BalanceQuantity</label>
        </div>
        <div className="field">
  <input
                    type="text"
                    name="Day_and_Date"
                    id="Day_and_Date"
                    value={input.Day_and_Date}
                    onChange={handleInputForm}
                    autoComplete="off"
                    placeholder="Enter Day and Date"
                  />
          <label>Day and Date</label>
        </div>
  <div className="field">
  <input
                    type="submit"
                    name="signin"
                    id="signin"
                    className="form-submit"
                    onClick={PostInput}
                    value="Feed"
                  />
        </div>
        <div className="field">
  <a href="./display">See Details</a>
        </div>
  </form>
  </div>
        </body>
        </html>
      
    )
}

export default AdminStoreForm
